
public class QuotientAndRemainder {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int num1 = 150;
        int num2 = 50;
        int quot = num1/num2;
        int remaind = num1%num2;
        System.out.println("Quotient is: " + quot);
        System.out.println("Remainder is: " + remaind);
	}

}
